import React from "react";
import Nav from "./components/Nav/Nav";
import Footer from "./components/Footer/Footer";
import Carousel from "./components/Carousel/Carousel";
import styles from "./Program.module.css";
import britain_flag from "./assets/images/britain_flag.png";
import us_flag from "./assets/images/us_flag.png";
import europe_flag from "./assets/images/europe_flag.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChalkboardTeacher,
  faUser,
  faBook,
  faQuoteLeft,
  faQuoteRight,
} from "@fortawesome/free-solid-svg-icons";
import "react-multi-carousel/lib/styles.css";

class Program extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      name: "",
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  }

  render() {
    return (
      <div className={styles.Program}>
        <Nav />
        <header>
          <div className={styles.header_text}>
            <h1 className={styles.title}>Our Program</h1>
            <p className={styles.cta}>
              Learn more about individual schools and their application process.
            </p>
          </div>
        </header>
        <main>
          <section className={styles.video}>
            <h2>We Support You</h2>
            <div>
              <p>
                Project Access Austria accompanies people on their way and helps
                them overcome challenges. Our work in as a non-profit
                organization is geared towards developing holistic approaches to
                some of the greatest challenges facing our society. To be able
                to act and to remain so is of fundamental importance. With our
                projects we support individuals and communities on their way.
              </p>
            </div>
          </section>
          <section className={styles.infographic}>
            <div className={styles.help_container}>
              <h2>How we can help you</h2>
              <div className={styles.left}>
                <FontAwesomeIcon icon={faUser} className={styles.icon} />
                <p>
                  Work with an individual mentor currently attending one of our
                  target universities to get personalized feedback on your
                  application.
                </p>
              </div>
              <div className={styles.right}>
                <FontAwesomeIcon
                  icon={faChalkboardTeacher}
                  className={styles.icon}
                />
                <p>
                  Attend our Bootcamp, a three day university application
                  seminar, and acquire the skills needed to craft a successful
                  application.
                </p>
              </div>
              <div className={styles.left}>
                <FontAwesomeIcon icon={faBook} className={styles.icon} />
                <p>
                  Take our preperation and follow-up courses, where we will help
                  you along the way to your target university: from orientation,
                  to the selection of suitable courses, to interview training.
                </p>
              </div>
              <button>
                <a href="/">Learn More</a>
              </button>
            </div>
          </section>
          <section className={styles.video}></section>
          <section className={styles.testimonials_signup}>
            <div className={styles.testimonials}>
              <FontAwesomeIcon
                icon={faQuoteRight}
                className={styles.quote_right}
              />
              <FontAwesomeIcon
                icon={faQuoteLeft}
                className={styles.quote_left}
              />
              <Carousel />
            </div>
            <div className={styles.signup}>
              <h3>Sign Up Here for Help with Your Applications</h3>
              <p>
                To find out more about how Project Access can help you, we only
                ask for your first name and your email address to send you more
                information. Just fill out the form below and we will get in
                touch!
              </p>
              <form onSubmit={this.handleSubmit}>
                <label for="name">First and Last Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleChange}
                  required
                />
                <label for="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={this.state.email}
                  onChange={this.handleChange}
                  required
                />
                <button type="submit">Register</button>
              </form>
            </div>
          </section>
        </main>
        <Footer />
      </div>
    );
  }
}

export default Program;
