// export default MultiCarousel;

const MultiCarousel = () => {
  let plugins = [new Fade()];
  return (
    <Flicking
      tag="div"
      viewportTag="div"
      cameraTag="div"
      classPrefix="eg-flick"
      deceleration={0.0075}
      horizontal={true}
      circular={false}
      infinite={true}
      infiniteThreshold={0}
      lastIndex={Infinity}
      threshold={40}
      duration={100}
      panelEffect={(x) => 1 - Math.pow(1 - x, 3)}
      defaultIndex={0}
      inputType={["touch", "mouse"]}
      thresholdAngle={45}
      bounce={10}
      autoResize={false}
      adaptive={false}
      bound={false}
      overflow={false}
      hanger={"50%"}
      anchor={"50%"}
      gap={0}
      moveType={{ type: "snap", count: 1 }}
      collectStatistics={false}
    >
      <div>
        <img src={header_img1} alt="header" />
      </div>
      <div>
        <img src={header_img2} alt="header" />
      </div>
      <div>
        <img src={header_img3} alt="header" />
      </div>
      <div>
        <img src={header_img4} alt="header" />
      </div>
    </Flicking>
  );
};

export default MultiCarousel;
