import React from "react";
import Nav from "./components/Nav/Nav";
import Footer from "./components/Footer/Footer";
import Carousel from "./components/Carousel/Carousel";
import MultiCarousel from "./components/MultiCarousel/MultiCarousel";
import "./Home.css";
import britain_flag from "./assets/images/britain_flag.png";
import us_flag from "./assets/images/us_flag.png";
import europe_flag from "./assets/images/europe_flag.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChalkboardTeacher,
  faUser,
  faBook,
  faQuoteLeft,
  faQuoteRight,
} from "@fortawesome/free-solid-svg-icons";
import "react-multi-carousel/lib/styles.css";
import {
  FlickingEvent,
  SelectEvent,
  ChangeEvent,
  NeedPanelEvent,
} from "@egjs/flicking";
import Flicking from "@egjs/react-flicking";
import { Fade, AutoPlay } from "@egjs/flicking-plugins";
import header_img1 from "./assets/images/header_img1.jpg";
import header_img2 from "./assets/images/header_img2.jpg";
import header_img3 from "./assets/images/header_img3.jpg";
import header_img4 from "./assets/images/header_img4.jpg";

function Home() {
  return (
    <div className="Home">
      <Nav />
      <header>
        <div className="header_text">
          <Flicking
            tag="div"
            viewportTag="div"
            cameraTag="div"
            onNeedPanel={(e: NeedPanelEvent) => {}}
            onMoveStart={(e: FlickingEvent) => {}}
            onMove={(e: FlickingEvent) => {}}
            onMoveEnd={(e: FlickingEvent) => {}}
            onHoldStart={(e: FlickingEvent) => {}}
            onHoldEnd={(e: FlickingEvent) => {}}
            onRestore={(e: FlickingEvent) => {}}
            onSelect={(e: SelectEvent) => {}}
            onChange={(e: ChangeEvent) => {}}
            classPrefix="eg-flick"
            deceleration={0.0075}
            horizontal={true}
            circular={false}
            infinite={true}
            infiniteThreshold={0}
            lastIndex={Infinity}
            threshold={40}
            duration={100}
            panelEffect={(x) => 1 - Math.pow(1 - x, 3)}
            defaultIndex={0}
            inputType={["touch", "mouse"]}
            thresholdAngle={45}
            bounce={10}
            autoResize={false}
            adaptive={false}
            bound={false}
            overflow={false}
            hanger={"50%"}
            anchor={"50%"}
            gap={0}
            moveType={{ type: "snap", count: 1 }}
            collectStatistics={false}
          >
            <div>
              <img src={header_img1} alt="header" />
            </div>
            <div>panel 1</div>
            <div>panel 2</div>
          </Flicking>
          <h1 className="title">Project Access Austria</h1>
          <p className="cta">Apply to top universities.</p>
          <p>We'll help you - free of charge.</p>
        </div>
      </header>
      <main>
        <section className="target_uni">
          <h2>Target Universities</h2>
          <p>
            Our expertise lies in the application to so-called "top
            universities", i.e. selective universities that are characterized by
            high research output.
          </p>
          <div className="regions">
            <div className="region">
              <img src={britain_flag} className="flag" alt="britain flag" />
              <h4>Great Britain</h4>
              <p>
                Cambridge University, Durham University, Imperial College
                London, Kingâ€™s College London, London School of Economics and
                Political Science, Oxford University, University College London,
                University of Warwick
              </p>
            </div>
            <div className="region middle">
              <img src={us_flag} className="flag" alt="us flag" />
              <h4>United States</h4>
              <p>
                Brown University, Columbia University, Cornell University,
                Dartmouth, Harvard University, Massachusetts Institute of
                Technology, New York University, Princeton University, Stanford
                University, UC Berkeley, University of Pennsylvania, Yale
                University
              </p>
            </div>
            <div className="region">
              <img src={europe_flag} className="flag" alt="europe flag" />
              <h4>Continental Europe</h4>
              <p>Coming Soon</p>
            </div>
          </div>
        </section>
        <section className="help">
          <div className="help_container">
            <h2>How we can help you</h2>
            <div className="left">
              <FontAwesomeIcon icon={faUser} className="icon" />
              <p>
                Work with an individual mentor currently attending one of our
                target universities to get personalized feedback on your
                application.
              </p>
            </div>
            <div className="right">
              <FontAwesomeIcon icon={faChalkboardTeacher} className="icon" />
              <p>
                Attend our Bootcamp, a three day university application seminar,
                and acquire the skills needed to craft a successful application.
              </p>
            </div>
            <div className="left">
              <FontAwesomeIcon icon={faBook} className="icon" />
              <p>
                Take our preperation and follow-up courses, where we will help
                you along the way to your target university: from orientation,
                to the selection of suitable courses, to interview training.
              </p>
            </div>
            <button>
              <a href="">Learn More</a>
            </button>
          </div>
        </section>
        <section className="testimonials_signup">
          <div className="testimonials">
            <FontAwesomeIcon icon={faQuoteRight} className="quote_right" />
            <FontAwesomeIcon icon={faQuoteLeft} className="quote_left" />
            <Carousel />
          </div>
          <div className="signup">
            <h3>Sign Up Here for Help with Your Applications</h3>
            <p>
              To find out more about how Project Access can help you, we only
              ask for your first name and your email address to send you more
              information. Just fill out the form below and we will get in
              touch!
            </p>
            <form>
              <label for="name">First and Last Name</label>
              <input className="name" id="name" />
              <label for="email">Email</label>
              <input className="email" id="email" />
              <button type="submit">Register</button>
            </form>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
}

export default Home;
