import React from "react";
import Nav from "./components/Nav/Nav";
import Footer from "./components/Footer/Footer";
import Carousel from "./components/Carousel/Carousel";
import styles from "./Home.module.css";
import britain_flag from "./assets/images/britain_flag.png";
import us_flag from "./assets/images/us_flag.png";
import europe_flag from "./assets/images/europe_flag.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChalkboardTeacher,
  faUser,
  faBook,
  faQuoteLeft,
  faQuoteRight,
} from "@fortawesome/free-solid-svg-icons";
import "react-multi-carousel/lib/styles.css";

class Bootcamp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      name: "",
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  }

  render() {
    return (
      <div className={styles.Home}>
        <Nav />
        <header>
          <div className={styles.header_text}>
            <h1 className={styles.title}>Bootcamp</h1>
            <p className={styles.cta}>
              A free seminar that prepares you for the top university
              applications.
            </p>
          </div>
        </header>
        <main>
          <section className={styles.target_uni}>
            <h2>How it Works</h2>
            <p>
              In a seminar group of 8-10 people, you will learn from our
              trainers, who are studying at one of our target universities, how
              you can apply successfully. The exact program varies for each
              participant depending on your seminar group and target school.
            </p>
          </section>
          <section className={styles.target_uni}>
            <h2>Bootcamp 2021</h2>
            <p>
              Bootcamp 2020 took place from July 31st to August 2nd. The details
              for the boot camp 2021 are currently being worked out. You can
              already register for the Bootcamp 2021! ​ In 2021, the bootcamp
              will be open to Master and PhD applicants for the first time. We
              will organize a seminar group of 8-12 participants for "Graduate"
              applicants.
            </p>
            <button>Register</button>
          </section>
          <section className={styles.help}>
            <div className={styles.help_container}>
              <h2>The Schedule</h2>
              <div className={styles.row}>
                <div className={styles.left}>
                  <p>
                    Work with an individual mentor currently attending one of
                    our target universities to get personalized feedback on your
                    application.
                  </p>
                </div>
                <div className={styles.right}>
                  <p>
                    Attend our Bootcamp, a three day university application
                    seminar, and acquire the skills needed to craft a successful
                    application.
                  </p>
                </div>
              </div>
            </div>
          </section>
          <section className={styles.faq}></section>
          <section className={styles.testimonials_signup}>
            <div className={styles.testimonials}>
              <FontAwesomeIcon
                icon={faQuoteRight}
                className={styles.quote_right}
              />
              <FontAwesomeIcon
                icon={faQuoteLeft}
                className={styles.quote_left}
              />
              <Carousel />
            </div>
            <div className={styles.signup}>
              <h3>Sign Up Here for Help with Your Applications</h3>
              <p>
                To find out more about how Project Access can help you, we only
                ask for your first name and your email address to send you more
                information. Just fill out the form below and we will get in
                touch!
              </p>
              <form onSubmit={this.handleSubmit}>
                <label for="name">First and Last Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleChange}
                  required
                />
                <label for="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={this.state.email}
                  onChange={this.handleChange}
                  required
                />
                <button type="submit">Register</button>
              </form>
            </div>
          </section>
        </main>
        <Footer />
      </div>
    );
  }
}

export default Bootcamp;
